/**
 * 获取根据flexibleRem比例计算的px值
 * @param pxNum px值
 * @return {number} 对应比例的px值
 */
function getPx(pxNum, remUnit) {
    if (remUnit === void 0) { remUnit = 75; }
    if (typeof window === 'undefined') {
        throw new Error('window is undefined');
    }
    // @ts-ignore
    var flexibleRem = window.lib.flexible.rem;
    var result = pxNum;
    if (!isNaN(flexibleRem)) {
        result = (result / remUnit) * flexibleRem;
        // 保留2位小数值，防止安卓端渲染异常
        result = Math.floor(result * 100) / 100;
    }
    return result;
}
export default getPx;
