/**
 * 处理 restfull url接口
 * 如:
 * /teacher/:courseId/subCourse
 * 转为
 * /teacher/f347951525f34441ac6d537b17202660/subCourse
 * @param {string} url 要处理的url
 * @param {object} data
 * @returns {string}
 */
var parseRestUrl = function (url, data) {
    var reg = /\/:(\w+)/gi;
    var routeParams = url.match(reg);
    if (!data || routeParams === null) {
        return url;
    }
    routeParams.forEach(function (routeParam) {
        var keyName = routeParam.replace('/:', '');
        if (data[keyName]) {
            url = url.replace(':' + keyName, data[keyName]);
        }
    });
    return url;
};
export default parseRestUrl;
