import compareStrings from '../compareStrings';

describe('测试 compareStrings 工具函数', () => {
  test('1. 测试各种情况', () => {
    expect(compareStrings('3.0.0', '2.7.31')).toBe(1);
    expect(compareStrings('3.0.1', '3.0.0')).toBe(1);
    expect(compareStrings('3.0.0.0', '3.0.0')).toBe(0);
    expect(compareStrings('3.0.0', '3.0.0.0')).toBe(0);
    expect(compareStrings('3.0.1', '3.0.0.0')).toBe(1);
    expect(compareStrings('3.0.1', '3.0.1.0')).toBe(0);
    expect(compareStrings('3.0.10', '3.0.9')).toBe(1);
    expect(compareStrings('3.1.0', '3.0.9')).toBe(1);
    expect(compareStrings('3-1-0', '3-0-9', '-')).toBe(1);
    expect(compareStrings('3.0.0', '3.0.0')).toBe(0);
    expect(compareStrings('3.0.1', '3.0.1.0')).toBe(0);
    expect(compareStrings('2.7.31', '3.0.0')).toBe(-1);
    expect(compareStrings('3.0.0', '3.0.0.1')).toBe(-1);
    expect(compareStrings('3.0.0-b298df7', '3.0.0.1')).toBe(-1);
    expect(compareStrings('3.0.0-b298df7', '3.0.0')).toBe(0);
    expect(compareStrings('3.0.0-98e3df7', '3.0.0.0')).toBe(0);
  });
});