// test
import dateFormat from '../dateFormat';

describe('测试 dateFormat 函数', () => {
  const d = new Date();
  const year = d.getFullYear();
  const month = `${d.getMonth() + 1}`.padStart(2, '0');
  const date = `${d.getDate()}`.padStart(2, '0');
  const hour =  `${d.getHours()}`.padStart(2, '0');
  const minute =  `${d.getMinutes()}`.padStart(2, '0');
  const second =  `${d.getSeconds()}`.padStart(2, '0');
  it('1.转换日期', () => {
    expect(dateFormat('yyyy-MM-dd', d)).toBe(`${year}-${month}-${date}`);
  });

  it('2.格式为空', () => {
    expect(dateFormat('', d)).toBe(`${year}-${month}-${date} ${hour}:${minute}:${second}`);
  })

  it('2.格式中不包含y', () => {
    expect(dateFormat('MM-dd', d)).toBe(`${month}-${date}`);
  })

  it('2.格式中某项长度为1', () => {
    expect(dateFormat('M', d)).toBe(`${d.getMonth() + 1}`);
  })
});