import { dataURL2Blob } from '../file-util';
const dataUrl = 'data:image/png;base64,';

const invalidDataURl = 'hello';
const noTypeUrl = 'noType,';


describe('测试 file-util', () => {
  it('测试 dataURL2Blob 函数', () => {
    expect(() => dataURL2Blob(invalidDataURl)).toThrow();
    expect(dataURL2Blob(dataUrl)).not.toBe(undefined);
    expect(Object.prototype.toString.call(dataURL2Blob(dataUrl))).toBe('[object Blob]');
    expect(Object.prototype.toString.call(dataURL2Blob(noTypeUrl))).toBe('[object Blob]');
  })
})