import getPx from '../../getPx';

describe('测试 getPx 函数', () => {
  it('1. 可以转换', () => {
    // @ts-ignore
    window.lib = { flexible: { rem: 100 } };
    expect(getPx(375)).toBe(500)
  })

  it('2. 有 remUnit ，也可以转换', () => {
    // @ts-ignore
    window.lib = { flexible: { rem: 100 } };
    expect(getPx(375, 37.5)).toBe(1000)
  })

  it('3. rem不是数字，为转换', () => {
    // @ts-ignore
    window.lib = { flexible: { rem: 'hello' } };
    expect(getPx(375)).toBe(375);
  })
})