import isHttp from '../isHttp';

describe('测试 isHttp 函数', () => {
  it('1. 空符串', () => {
    expect(isHttp('')).toBe(false);
  });

  it('2. undefined', () => {
    expect(isHttp()).toBe(false);
  });

  it('3. null', () => {
    expect(isHttp(null)).toBe(false);
  });

  it('4. 非 http', () => {
    expect(isHttp('aaa')).toBe(false);
  });

  it('5. http', () => {
    expect(isHttp('http://asdf.com')).toBe(true);
  });

  it('6. https', () => {
    expect(isHttp('https://asdf.com')).toBe(true);
  });

  it('7. 不是以 https 开头', () => {
    expect(isHttp('abcdhttps://asdf.com')).toBe(false);
  });
})