import isPhoneNum from '../isPhoneNum';

describe('测试 isPhoneNum 函数', () => {
  it('1. 空符串', () => {
    expect(isPhoneNum('')).toBe(false);
  });

  it('2. undefined', () => {
    expect(isPhoneNum()).toBe(false);
  });

  it('3. null', () => {
    expect(isPhoneNum(null)).toBe(false);
  });

  it('4. 非 手机号: aaa', () => {
    expect(isPhoneNum('aaa')).toBe(false);
  });

  it('5. 非 手机号: 156227463', () => {
    expect(isPhoneNum('156227463')).toBe(false);
  });

  it('6. 非 手机号: 156227463177', () => {
    expect(isPhoneNum('156227463177')).toBe(false);
  });

  it('7. 手机号: 15622746317', () => {
    expect(isPhoneNum('15622746317')).toBe(true);
  });

  it('8. 手机号: 18912341234', () => {
    expect(isPhoneNum('18912341234')).toBe(true);
  });

  it('9. 手机号: 12341234，一串非手机号数字', () => {
    expect(isPhoneNum(12341234)).toBe(false);
  });

  it('10. 手机号: 0，一串非手机号数字', () => {
    expect(isPhoneNum(0)).toBe(false);
  });

  it('11. 手机号: 18912341234，一串数字', () => {
    expect(isPhoneNum(18912341234)).toBe(true);
  });
})