import makePageResumeToBottomWhenInputBlur from '../../makePageResumeToBottomWhenInputBlur';

describe('window 环境，测试 makePageResumeToBottomWhenInputBlur 函数', () => {
  
  it('1.测试执行 参数不填 ', () => {
    const spyScrollTo = spyOn(window, 'scrollTo');
    expect(spyScrollTo).not.toBeCalled();
    makePageResumeToBottomWhenInputBlur();
    expect(document.body.scrollTop).toBe(0);
    expect(spyScrollTo).toBeCalled();
  });

  it('2.测试执行 参数为 10 ', () => {
    const spyScrollTo = spyOn(window, 'scrollTo');
    expect(spyScrollTo).not.toBeCalled();
    makePageResumeToBottomWhenInputBlur(10);
    expect(document.body.scrollTop).toBe(10);
    expect(spyScrollTo).toBeCalled();
  });

})