import trimStr from '../trimStr';

describe('测试函数功能', () => {
  it('1. 超出长度，有自定义填充字符', () => {
    expect(trimStr('班级优化大师', 5, '.....')).toBe('班级优化.....')
  })

  it('2. 超出长度，没有自定义填充字符', () => {
    expect(trimStr('班级优化大师', 5)).toBe('班级优化...')
  })

  it('3. 没有超出长度', () => {
    expect(trimStr('班级优化大师', 6)).toBe('班级优化大师')
  })
})
