import unFormatPhoneValue from '../unFormatPhoneValue';


describe('测试 unFormatPhoneValue', () => {
  it('1.测试 非字符串', () => {
    expect(unFormatPhoneValue()).toBe('');
    expect(unFormatPhoneValue(null)).toBe('');
    expect(unFormatPhoneValue(123)).toBe('');
  })

  it('2.测试 字符串', () => {
    expect(unFormatPhoneValue('1234')).toBe('1234');
    expect(unFormatPhoneValue('12345678910')).toBe('12345678910');
    expect(unFormatPhoneValue('123-4567-8910')).toBe('12345678910');
    expect(unFormatPhoneValue('123 4567 8910')).toBe('12345678910');
  })
})