/**
 * @description 时间格式化
 * @param fmt 时间格式，如 yyyy-MM-dd hh:mm:ss
 * @param time 需要格式化的时间。如果是时间戳，单位是毫秒
 * @returns 返回按照fmt格式化后的字符串，如2018-12-08 12:35:30
 */
function dateFormat(fmt: string, time: number | string | Date) {
  //author: meizz
  var date = new Date(time);
  if (fmt === undefined || fmt === '') {
    fmt = 'yyyy-MM-dd hh:mm:ss';
  }
  var o = {
    'M+': date.getMonth() + 1, //月份
    'd+': date.getDate(), //日
    'h+': date.getHours(), //小时
    'm+': date.getMinutes(), //分
    's+': date.getSeconds(), //秒
    'q+': Math.floor((date.getMonth() + 3) / 3), //季度
    S: date.getMilliseconds() //毫秒
  };
  if (/(y+)/.test(fmt)) {
    fmt = fmt.replace(
      RegExp.$1,
      (date.getFullYear() + '').substr(4 - RegExp.$1.length)
    );
  }
  for (var k in o) {
    if (new RegExp('(' + k + ')').test(fmt)) {
      fmt = fmt.replace(
        RegExp.$1,
        RegExp.$1.length === 1
          ? (<any>o)[k]
          : ('00' + (<any>o)[k]).substr(('' + (<any>o)[k]).length)
      );
    }
  }
  return fmt;
};

export default dateFormat;