function dataURL2Blob(dataURL: string) {
  const parts = dataURL.split(',');
  const data = window.atob(parts[1]);
  const typeMatch = parts[0].match(/:(.*?);/);
  const type = (typeMatch && typeMatch[1]) || '';
  const blobPart = new Uint8Array(data.length);
  for (let i = 0; i < data.length; i++) {
    blobPart[i] = data.charCodeAt(i);
  }

  return new Blob([blobPart], { type });
}

export { dataURL2Blob };
