const phoneReg = /^[1][0-9]{10}$/;

/**
 * 字符串是否为合格的手机号格式
 * @param {string} phoneNum 手机号
 */
function isPhoneNum(phoneNum?: string | number | null) {
  if (typeof phoneNum === 'number') {
    phoneNum = phoneNum.toString();
  }
  if (!phoneNum) {
    return false;
  }
  return phoneReg.test(phoneNum);
}

export default isPhoneNum;