/**
 * @description 创建一个指定时间超时的promise，可自定义 reject 的内容
 * @param {number} [timeout=3000]
 * @param {string} [message='promise timeout']
 */
function timeoutPromise(timeout: number = 3000, message: string = 'promise timeout') {
  // @ts-ignore
  return new Promise((resolve, reject) => {
    setTimeout(() => {
      reject(message);
    }, timeout);
  });
}

export default timeoutPromise;

