/**
 * @description 对超过长度的字符串进行裁剪，并在尾部填充符号，如...
 * @param str 需要处理的字符串
 * @param len 结果字符串最大长度
 * @param padding 超出时尾部添加的内容
 */
function trimStr(str: string, len: number, padding: string = '...') {
  let validStr = str;
  if(validStr.length > len) {
    validStr = `${validStr.slice(0, len - 1)}${padding}`
  }

  return validStr;
}

export default trimStr;